import os
import shutil

import whisper
from audioread import audio_open

from classes.notion import NotionPage
from classes.recordings import Language, Owner, Recording

from .recordings import Language, Owner, Recording


def get_folder_meta(path:str) -> tuple[str, str]:
    chunks = path.split('_')
    assert len(chunks) == 2
    owner = chunks[0].lower()
    language = chunks[1].lower()
    return owner, language

def get_recordings(recordings_folder_path:str) -> list[Recording]:
    recordings = []
    for folder in os.listdir(recordings_folder_path):
        owner, language = get_folder_meta(folder)
        
        personal_folder = os.path.join(recordings_folder_path, folder)
        for subject_folder in os.listdir(personal_folder):
            subject_folder_path = os.path.join(personal_folder, subject_folder)
            for file in os.listdir(subject_folder_path):
                file_path = os.path.join(subject_folder_path, file)
                # check if file is a folder
                if os.path.isdir(file_path):
                    continue
                filename, extension = os.path.splitext(file)
                if extension == ".mp3" or extension == ".m4a":
                    with audio_open(file_path) as f:
                        duration = int(f.duration)
                    recording = Recording(file_path, filename, Language(language), Owner(owner), duration, subject_folder)
                    recordings.append(recording)
    return recordings

def get_chunks_from_transcription(segments:list[str], max_words:int) -> list[str]:
    chunks = []
    current_chunk = ""
    current_chunk_words = 0
    for segment in segments:
        num_words = len(segment.split())
        segment += ". "
        if current_chunk_words + num_words <= max_words:
            current_chunk += segment
            current_chunk_words += num_words
        else:
            chunks.append(current_chunk)
            current_chunk = segment
            current_chunk_words = num_words
    if current_chunk != "":
        chunks.append(current_chunk)
    return chunks

def transcribe_audio(model:whisper.Whisper, audio_path:str) -> str:
    result = model.transcribe(audio_path)
    return result['text']

def move_to_folder(audio_path:str, folder:str) -> None:
    folder_path = os.path.dirname(audio_path)
    processed_folder = os.path.join(folder_path, folder)
    if not os.path.exists(processed_folder):
        os.makedirs(processed_folder)
    shutil.move(audio_path, processed_folder)

